<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Registro - UniversoDev</title>
    @vite(['resources/css/app.css', 'resources/js/app.js'])
    <style>
        .animated-bg {
            position: fixed;
            inset: 0;
            overflow: hidden;
            z-index: 0;
            background: linear-gradient(135deg, #0f0e17 0%, #1a1825 50%, #0f0e17 100%);
        }

        .animated-bg::after {
            content: '';
            position: absolute;
            inset: 0;
            backdrop-filter: blur(8px);
            -webkit-backdrop-filter: blur(8px);
            background: rgba(15, 14, 23, 0.4);
        }

        .laravel-logo {
            position: absolute;
            opacity: 0.12;
            will-change: transform;
            animation: float linear infinite;
        }

        @keyframes float {
            0% {
                transform: translateY(100vh) rotate(0deg);
            }
            100% {
                transform: translateY(-100px) rotate(360deg);
            }
        }

        .laravel-logo svg {
            width: 100%;
            height: 100%;
            fill: currentColor;
        }

        .laravel-logo:nth-child(1) { left: 5%; width: 60px; height: 60px; animation-duration: 25s; animation-delay: 0s; color: #7c3aed; }
        .laravel-logo:nth-child(2) { left: 15%; width: 40px; height: 40px; animation-duration: 30s; animation-delay: -5s; color: #06b6d4; }
        .laravel-logo:nth-child(3) { left: 25%; width: 80px; height: 80px; animation-duration: 35s; animation-delay: -10s; color: #7c3aed; }
        .laravel-logo:nth-child(4) { left: 35%; width: 50px; height: 50px; animation-duration: 28s; animation-delay: -3s; color: #06b6d4; }
        .laravel-logo:nth-child(5) { left: 45%; width: 70px; height: 70px; animation-duration: 32s; animation-delay: -8s; color: #7c3aed; }
        .laravel-logo:nth-child(6) { left: 55%; width: 45px; height: 45px; animation-duration: 27s; animation-delay: -12s; color: #06b6d4; }
        .laravel-logo:nth-child(7) { left: 65%; width: 65px; height: 65px; animation-duration: 33s; animation-delay: -2s; color: #7c3aed; }
        .laravel-logo:nth-child(8) { left: 75%; width: 55px; height: 55px; animation-duration: 29s; animation-delay: -15s; color: #06b6d4; }
        .laravel-logo:nth-child(9) { left: 85%; width: 75px; height: 75px; animation-duration: 31s; animation-delay: -7s; color: #7c3aed; }
        .laravel-logo:nth-child(10) { left: 92%; width: 48px; height: 48px; animation-duration: 26s; animation-delay: -11s; color: #06b6d4; }
        .laravel-logo:nth-child(11) { left: 10%; width: 52px; height: 52px; animation-duration: 34s; animation-delay: -18s; color: #7c3aed; }
        .laravel-logo:nth-child(12) { left: 30%; width: 42px; height: 42px; animation-duration: 28s; animation-delay: -20s; color: #06b6d4; }
        .laravel-logo:nth-child(13) { left: 50%; width: 68px; height: 68px; animation-duration: 36s; animation-delay: -14s; color: #7c3aed; }
        .laravel-logo:nth-child(14) { left: 70%; width: 38px; height: 38px; animation-duration: 24s; animation-delay: -22s; color: #06b6d4; }
        .laravel-logo:nth-child(15) { left: 90%; width: 58px; height: 58px; animation-duration: 30s; animation-delay: -16s; color: #7c3aed; }

        .content-wrapper {
            position: relative;
            z-index: 10;
            min-height: 100vh;
            display: flex;
            align-items: center;
            justify-content: center;
            padding: 2rem 1rem;
        }

        .register-container {
            width: 100%;
            max-width: 56rem;
        }
    </style>
</head>
